import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import VotingABI from "./VotingABI.json";

// Replace with your deployed contract address
const CONTRACT_ADDRESS = "REPLACE_WITH_YOUR_CONTRACT_ADDRESS";

function App() {
  const [provider, setProvider] = useState(null);
  const [signer, setSigner] = useState(null);
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState(null);
  const [parties, setParties] = useState([]);
  const [loading, setLoading] = useState(false);
  const [txProcessing, setTxProcessing] = useState(false);

  useEffect(() => {
    if (window.ethereum) {
      const prov = new ethers.BrowserProvider(window.ethereum);
      setProvider(prov);
    } else {
      alert("Install MetaMask to use this app!");
    }
  }, []);

  useEffect(() => {
    if (provider && CONTRACT_ADDRESS !== "REPLACE_WITH_YOUR_CONTRACT_ADDRESS") {
      const c = new ethers.Contract(CONTRACT_ADDRESS, VotingABI, provider);
      setContract(c);
      loadParties(c);
    }
  }, [provider]);

  async function connectWallet() {
    if (!window.ethereum) return alert("Install MetaMask");

    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setAccount(accounts[0]);

      const signer = await provider.getSigner();
      setSigner(signer);

      if (CONTRACT_ADDRESS && VotingABI) {
        const contractWithSigner = new ethers.Contract(CONTRACT_ADDRESS, VotingABI, signer);
        setContract(contractWithSigner);
        await loadParties(contractWithSigner);
      }
    } catch (err) {
      console.error(err);
      alert("Failed to connect wallet: " + (err.message || JSON.stringify(err)));
    }
  }

  async function loadParties(c) {
    try {
      setLoading(true);
      const res = await c.getParties();
      const names = res[0];
      const counts = res[1].map(n => n.toString());
      const arr = names.map((name, i) => ({ name, count: counts[i], index: i }));
      setParties(arr);
    } catch (err) {
      console.error(err);
      alert("Failed to load parties: " + (err.message || JSON.stringify(err)));
    } finally {
      setLoading(false);
    }
  }

  async function handleVote(index) {
    if (!signer) return alert("Connect wallet first");
    if (!contract) return alert("Contract not loaded");

    try {
      setTxProcessing(true);
      const c = contract.connect ? contract.connect(signer) : contract;
      const tx = await c.vote(index);
      await tx.wait();
      alert("Vote successful!");
      await loadParties(c);
    } catch (err) {
      console.error(err);
      const reason = err?.data?.message || err?.reason || err?.message || JSON.stringify(err);
      alert("Transaction failed: " + reason);
    } finally {
      setTxProcessing(false);
    }
  }

  return (
    <div className="min-h-screen bg-[#1a1a2e] text-white flex flex-col items-center py-10 font-inter">
      <div className="container max-w-3xl">
        <header className="text-center mb-8">
          <h1 className="text-4xl font-bold mb-4">On-Chain Voting</h1>
          {!account ? (
            <button
              className="btn btn-primary transition-transform transform hover:scale-105"
              onClick={connectWallet}
            >
              Connect Wallet
            </button>
          ) : (
            <div className="text-lg">Connected: {account}</div>
          )}
        </header>

        <div className="space-y-4">
          {loading && <p className="text-center">Loading parties...</p>}
          {!loading && parties.length === 0 && <p className="text-center">No parties available yet.</p>}

          {parties.map((p) => (
            <div
              key={p.index}
              className="candidate-card flex justify-between items-center p-4 rounded-lg shadow-lg hover:scale-105 transition-transform"
            >
              <span className="text-xl font-semibold">{p.name}</span>
              <div className="flex items-center space-x-4">
                <span className="text-lg">{p.count} Votes</span>
                {!txProcessing && (
                  <button
                    className="btn btn-outline hover:bg-[#e43f5a]/20 transition-all"
                    onClick={() => handleVote(p.index)}
                  >
                    Vote
                  </button>
                )}
              </div>
            </div>
          ))}

          {txProcessing && (
            <p className="text-center text-yellow-400 mt-4">Transaction in progress...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
